"use strict";

const BinaryStoreError = require("./binarystore-error.js");

class InvalidRevision extends BinaryStoreError {
  constructor(cause, message) {
    super(message || "Revision attribute is not valid. Entered value does not match value in database.", cause);
    this.code = `${BinaryStoreError.ERROR_PREFIX}invalidRevision`;
  }
}

module.exports = InvalidRevision;
