"use strict";

const BinaryStoreError = require("./binarystore-error.js");

class MissingRevision extends BinaryStoreError {
  constructor(cause, message) {
    super(
      message || "Revision attribute is missing. Attribute is mandatory when using revision based strategy.",
      cause
    );
    this.code = `${BinaryStoreError.ERROR_PREFIX}missingRevision`;
  }
}

module.exports = MissingRevision;
